						<div class="frm-fld text required">
							<label for="fld1">
								<span class="fld-label"><?php echo $lang_backup['Folder']; ?></span><br />
								<span class="fld-input"><input type="file" id="fld1" name="dump_file" size="50" maxlength="200" value="<?php echo $lang_backup['browse']?>" <?php echo $check_disable ? '' : 'disabled="disabled"'?> /></span>
							</label>
						</div>
							<div class="frm-buttons">
								<span class="submit">
									<input type="submit" class="button" name="upload" value="<?php echo $lang_backup['Upload']?>" <?php echo $check_disable ? '' : 'disabled="disabled"'?> />
								</span>
							</div>
							<label for="fld <?php echo ++$forum_page['set_count'] ?>">
								<input type="hidden" name="csrf_token" value="<?php echo generate_form_token(forum_link($link))?>" />
							</label>
					</fieldset>
					<?php

						if (isset($upload_result))
						{
							echo '<div class="frm-info"><p class="warn">';
							if ($upload_result == 2)
								echo sprintf($lang_backup['Up error'], trim($_FILES['dump_file']['name']));
							else if ($upload_result == 1)
								echo $lang_backup['Up success'];
							else
								echo $lang_backup['Up fail'];
							echo '</p></div>';
