extension.install:
$results = $forum_db->query('show columns from '.$forum_db->prefix.'users like \'username\'');
$username = $forum_db->fetch_assoc($results);

$results = $forum_db->query('show columns from '.$forum_db->prefix.'users like \'id\'');
$userid = $forum_db->fetch_assoc($results);

$schema = array(
'FIELDS'	=> array(
	'ip'		=> array(
		'datatype'		=> 'VARCHAR(15)',
		'allow_null'	=> false
	),
	'type'	=> array(
		'datatype'		=> 'VARCHAR(64)',
		'allow_null'	=> false
	),
	'comment'	=> array(
		'datatype'		=> 'VARCHAR(255)',
		'allow_null'	=> false
	),
	'user_id'	=> array(
		'datatype'		=> $userid['Type'],
		'allow_null'	=> true
	),
	'user_name'	=> array(
		'datatype'		=> $username['Type'],
		'allow_null'	=> true
	),
	'date'			=> array(
		'datatype'		=> 'TIMESTAMP',
		'allow_null'	=> false
	)
));

$forum_db->create_table('pun_admin_events', $schema);

hooks.hook:

if (file_exists($ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php'))
	require $ext_info['path'].'/lang/'.$forum_user['language'].'/'.$ext_info['id'].'.php';
else
	require $ext_info['path'].'/lang/English/'.$ext_info['id'].'.php';

if ((FORUM_PAGE_SECTION == 'management') && ($forum_user['g_id'] == FORUM_ADMIN))
{
	$forum_page['admin_submenu']['pun_events_management'] = '<li class="'.((FORUM_PAGE == 'admin-management-events') ? 'active' : 'normal').((empty($forum_page['admin_menu'])) ? ' first-item' : '').'"><a href="'.forum_link($forum_url['admin_management_events']).'">'.$lang_pun_admin_events['Events'].'</a></li>';
}
