extension.install:
		if (!$forum_db->table_exists('post_approval_users'))
		{
			$schema = array(
				'FIELDS'		=> array(
					'id'				=> array(
						'datatype'		=> 'SERIAL',
						'allow_null'	=> false
					),
					'group_id'			=> array(
						'datatype'		=> 'INT(10) UNSIGNED',
						'allow_null'	=> false,
						'default'		=> '3'
					),
					'username'			=> array(
						'datatype'		=> 'VARCHAR(200)',
						'allow_null'	=> false,
						'default'		=> '\'\''
					),
					'password'			=> array(
						'datatype'		=> 'VARCHAR(40)',
						'allow_null'	=> false,
						'default'		=> '\'\''
					),
					'salt'				=> array(
						'datatype'		=> 'VARCHAR(12)',
						'allow_null'	=> true
					),
					'email'				=> array(
						'datatype'		=> 'VARCHAR(80)',
						'allow_null'	=> false,
						'default'		=> '\'\''
					),
					'email_setting'		=> array(
						'datatype'		=> 'TINYINT(1)',
						'allow_null'	=> false,
						'default'		=> '1'
					),
					'timezone'			=> array(
						'datatype'		=> 'FLOAT',
						'allow_null'	=> false,
						'default'		=> '0'
					),
					'dst'				=> array(
						'datatype'		=> 'TINYINT(1)',
						'allow_null'	=> false,
						'default'		=> '0'
					),
					'language'			=> array(
						'datatype'		=> 'VARCHAR(25)',
						'allow_null'	=> false,
						'default'		=> '\'English\''
					),
					'style'				=> array(
						'datatype'		=> 'VARCHAR(25)',
						'allow_null'	=> false,
						'default'		=> '\'Oxygen\''
					),
					'registered'		=> array(
						'datatype'		=> 'INT(10) UNSIGNED',
						'allow_null'	=> false,
						'default'		=> '0'
