						'allow_null'	=> false,
						'default'		=> '0'
					),
					'activate_key'		=> array(
						'datatype'		=> 'VARCHAR(8)',
						'allow_null'	=> true
					),
				),
				'PRIMARY KEY'	=> array('id'),
				'INDEXES'		=> array(
					'registered_idx'	=> array('registered'),
					'username_idx'		=> array('username')
				)
			);

			$forum_db->create_table('post_approval_users', $schema);
		}

		if (!$forum_db->table_exists('post_approval_posts'))
		{
			$schema = array(
				'FIELDS'		=> array(
					'id'			=> array(
						'datatype'		=> 'SERIAL',
						'allow_null'	=> false
					),
					'poster'		=> array(
						'datatype'		=> 'VARCHAR(200)',
						'allow_null'	=> false,
						'default'		=> '\'\''
					),
					'poster_id'		=> array(
						'datatype'		=> 'INT(10) UNSIGNED',
						'allow_null'	=> false,
						'default'		=> '1'
					),
					'poster_ip'		=> array(
						'datatype'		=> 'VARCHAR(39)',
						'allow_null'	=> true
					),
					'poster_email'	=> array(
						'datatype'		=> 'VARCHAR(80)',
						'allow_null'	=> true
					),
					'message'		=> array(
						'datatype'		=> 'TEXT',
						'allow_null'	=> true
					),
					'hide_smilies'	=> array(
						'datatype'		=> 'TINYINT(1)',
						'allow_null'	=> false,
						'default'		=> '0'
					),
					'posted'		=> array(
						'datatype'		=> 'INT(10) UNSIGNED',
						'allow_null'	=> false,
						'default'		=> '0'
					),
					'edited'		=> array(
						'datatype'		=> 'INT(10) UNSIGNED',
						'allow_null'	=> true
					),
					'edited_by'		=> array(
						'datatype'		=> 'VARCHAR(200)',
						'allow_null'	=> true
					),
					'topic_id'		=> array(
						'datatype'		=> 'INT(10) UNSIGNED',
