extension.install:
if (!$forum_db->table_exists('pun_forum_news'))
{
	$schema = array(
		'FIELDS'	=> array(
			'id'	=>	array(
				'datatype'		=> 'SERIAL',
				'allow_null'	=> false
			),
			'poster'	=>	array(
				'datatype'		=> 'VARCHAR(200)',
				'allow_null'	=> false
			),
			'poster_id'	=>	array(
				'datatype'		=> 'INT(10) UNSIGNED',
				'allow_null'	=> false
			),
			'poster_e_mail'	=>	array(
				'datatype'		=> 'VARCHAR(80)',
				'allow_null'	=> false
			),
			'message' => array(
				'datatype'		=> 'TEXT',
				'allow_null'	=> false
			),
			'hide_smilies'	=>	array(
				'datatype'		=> 'TINYINT(1)',
				'allow_null'	=> false,
				'default'		=> '0'
			),
			'posted'		=> array(
				'datatype'		=> 'INT(10) UNSIGNED',
				'allow_null'	=> false,
				'default'		=> '0'
			),
		),
		'PRIMARY KEY'	=> array('id')
	);
	$forum_db->create_table('pun_forum_news', $schema);
}

$forum_db->add_field('posts', 'forum_news', 'tinyint(1)', TRUE);
$forum_db->add_field('groups', 'g_add_forum_news', 'tinyint(1)', TRUE);
