extension.install:
$schema = array(
	'FIELDS'		=> array(
		'id'			=> array(
			'datatype'		=> 'SERIAL',
			'allow_null'	=> false
		),
		'id_topic'		=> array(
			'datatype'		=> 'int(100)',
			'allow_null'	=> false
		),
		'question'			=> array(
			'datatype'		=> 'VARCHAR(150)',
			'allow_null'	=> true
		),
		'able_see'			=> array(
			'datatype'		=> 'int(100)',
			'allow_null'	=> true
		),
		'able_rev'		=> array(
			'datatype'		=> 'int(100)',
			'allow_null'	=> true
		),
		'able_start_day'		=> array(
			'datatype'		=> 'int(100)',
			'allow_null'	=> true
		),
		'able_end_day'	=> array(
			'datatype'		=> 'int(100)',
			'allow_null'	=> false
		)
	),
	'PRIMARY KEY'	=> array('id')
);
$forum_db->create_table('questions', $schema);

$schema = array(
	'FIELDS'		=> array(
		'id'			=> array(
			'datatype'		=> 'SERIAL',
			'allow_null'	=> false
		),
		'id_ques'		=> array(
			'datatype'		=> 'int(100)',
			'allow_null'	=> false
		),
		'answer'			=> array(
			'datatype'		=> 'VARCHAR(50)',
			'allow_null'	=> true
		)
	),
	'PRIMARY KEY'	=> array('id')
);
$forum_db->create_table('answers', $schema);

$schema = array(
	'FIELDS'		=> array(
		'id'			=> array(
			'datatype'		=> 'SERIAL',
			'allow_null'	=> false
		),
		'id_topic'		=> array(
			'datatype'		=> 'int(100)',
			'allow_null'	=> false
		),
		'user'			=> array(
			'datatype'		=> 'VARCHAR(50)',
			'allow_null'	=> true
		),
		'ball'			=> array(
			'datatype'		=> 'int(11)',
			'allow_null'	=> true
		)
	),
	'UNIQUE KEYS'	=> array('id' => array('id'))
);
$forum_db->create_table('voting', $schema);

$forum_db->add_field('groups', 'g_poll_add', 'int(100)', true, 1);

$query_poll_poll = array(
	'INSERT'	=> 'conf_name, conf_value',
	'INTO'		=> 'config',
	'VALUES'	=> '"p_pun_poll_disable_see","0"'
);

$forum_db->query_build($query_poll_poll) or error(__FILE__, __LINE__);

