				<div class="mf-item"><span class="fld-input"><input type="checkbox" id="fld <?php echo (++$forum_page['fld_count'])?>" name="dis_see" value="1" <?php echo ($forum_config['p_pun_poll_disable_see'] != '0') ? 'disabled' : (($poll_dis_see == 1) ? 'checked' : '') ?> /></span> <label for="fld<?php echo $forum_page['fld_count']?>"><?php echo $lang_pun_poll['See results']?></label></div>
				<div class="mf-item"><span class="fld-input"><input type="checkbox" id="fld <?php echo (++$forum_page['fld_count'])?>" name="dis_rev" value="1" <?php echo ($forum_config['p_pun_poll_disable_revote'] != '0') ? 'disabled' : (($poll_dis_rev == 1) ? 'checked' : '') ?> /></span> <label for="fld<?php echo $forum_page['fld_count']?>"><?php echo $lang_pun_poll['Able revote']?></label></div>


