if ($fid)
{

	$question = (isset($_POST['question_of_poll'])) ? $_POST['question_of_poll'] : '';

	if ($question != '')
	{

		$arr = $_POST['poll_answer'];
		$max_c = count($arr);
		$query_poll = array(
			'INSERT'	=> 'id_topic, question',
			'INTO'		=> 'questions',
			'VALUES'	=> $new_tid.', \''.$forum_db->escape($question).'\''
		);

		$forum_db->query_build($query_poll) or error(__FILE__, __LINE__);

		for ($iter = 0; $iter < count($arr); $iter++)
		{

			if ($arr[$iter] != '')
			{
				$query_poll = array(
					'INSERT'	=> 'id_ques, answer',
					'INTO'		=> 'answers',
					'VALUES'	=> $new_tid.', \''.$forum_db->escape($arr[$iter]).'\''
				);

				$forum_db->query_build($query_poll) or error(__FILE__, __LINE__);
			}
		}

		//If user set the date of poll
		if (isset($_POST['allow_poll_days']))
		{
			$today_poll = getdate();
			$query_poll = array(
				'UPDATE'	=> 'questions',
				'SET'		=> 'able_start_day='.time().', able_end_day='.intval((time() + intval($_POST['allow_poll_days']) * 3600 * 24)),
				'WHERE'		=> 'id_topic='.$new_tid
			);

			$forum_db->query_build($query_poll) or error(__FILE__, __LINE__);
		}
	}
}

if ($fid)
{

	$query_poll = array(
		'SELECT'	=> 'g_poll_add',
		'FROM'		=> 'groups AS g',
		'JOINS'		=> array(
			array(
				'JOIN'	=> 'users AS u',
				'ON'	=> '(u.group_id=g.g_id AND u.username=\''.$forum_user['username'].'\')'
			)
		)
	);

	$poll_result = $forum_db->query_build($query_poll) or error(__FILE__, __LINE__);

	$poll_add = $forum_db->fetch_assoc($poll_result);
	$days_poll = NULL;

	if (isset($_POST['allow_poll_days']))
	{
		if ($_POST['allow_poll_days'] != '')
			$days_poll = ($_POST['allow_poll_days'] > 0) ? $_POST['allow_poll_days'] : NULL;
	}

	if (($poll_add['g_poll_add'] == '1') || ($forum_user['is_admmod']))
	{
		$count_poll_ans = NULL;
		$query_poll = array(
			'SELECT'	=> 'conf_value',
			'FROM'		=> 'config',
			'WHERE'		=> 'conf_name=\'p_pun_poll_max_answers\''
		);

		$poll_result = $forum_db->query_build($query_poll) or error(__FILE__, __LINE__);

		if ($forum_db->num_rows($poll_result))
			$max_count = $forum_db->fetch_assoc($poll_result);

		$new_count = $forum_db->fetch_assoc($poll_result);

		?>

		<fieldset class="frm-set set<?php echo ++$forum_page['set_count'] ?>">
			<div class="frm-fld text textarea required">
				<label for="quest">
					<span class="fld-label"><?php echo $lang_pun_poll['Poll question'] ?></span><br />
					<span class="fld-input"><input type="text" id="quest" name="question_of_poll" size="84%" maxlength="150" value="<?php echo (isset($_POST['question_of_poll'])) ? htmlspecialchars($_POST['question_of_poll']) : '' ?>"></span><br />
				</label>
			</div>

			<?php

			$poll_array_answer = (isset($_POST['poll_answer'])) ? $_POST['poll_answer'] : NULL;

			if ($poll_array_answer != NULL)
			{
				if ($_POST['poll_ans_count'] < count($poll_array_answer))
				{
					$count_poll_ans = $_POST['poll_ans_count'];
					$tmp_count = count($poll_array_answer);

					for ($iter = $count_poll_ans + 1; $iter < $tmp_count; $iter++)
					{
						$poll_array_answer[$iter] = '';
					}
				}
				else
				{
					$count_poll_ans = intval($_POST['poll_ans_count']);
				}
			}

			if ($count_poll_ans < 2)
				$count_poll_ans = 2;

			if ($count_poll_ans > $max_count['conf_value'])
				$count_poll_ans = $max_count['conf_value'];

			for ($iter = 0; $iter < $count_poll_ans; $iter++)
			{

			?>

				<div class="frm-fld text textarea required">
					<label for="answer<?php echo $iter ?>">
						<span class="fld-label"><?php echo $lang_pun_poll['Voting answer'] ?></span><br />
						<span class="fld-input"><input type="text" id="poll_answer[]" name="poll_answer[]" size="84%" maxlength="50" value="<?php echo ((isset($poll_array_answer[$iter])) ? htmlspecialchars($poll_array_answer[$iter]) : '') ?>"></span><br />
					</label>
				</div>

			<?php

			}

			?>

			<div class="frm-fld text textarea required">
				<label for="allow_poll_days">
					<span class="fld-label"><?php echo $lang_pun_poll['Allow days'] ?></span><br />
					<span class="fld-input"><input type="text" name="allow_poll_days" <?php echo ($days_poll != NULL) ? ' value="'.$days_poll.'"' : '' ?>></span><br />
				</label>
			</div>
		</fieldset>
		<fieldset class="frm-set set<?php echo ++$forum_page['set_count'] ?>">
			<fieldset class="frm-group">
				<legend><span><?php echo $lang_pun_poll['Additional options'] ?></span></legend>
				<div class="radbox"><label for="first_option"><input type="checkbox" id="first_option" name="dis_see" <?php echo ($forum_config['p_pun_poll_disable_see'] != '0') ? 'disabled' : ((isset($_POST['dis_see'])) ? 'checked' : '') ?>> <?php echo $lang_pun_poll['See results'] ?></label></div>
				<div class="radbox"><label for="second_option"><input type="checkbox" id="second_option" name="dis_rev" <?php echo ($forum_config['p_pun_poll_disable_revote'] != '0') ? 'disabled' : ((isset($_POST['dis_rev'])) ? 'checked' : '') ?>> <?php echo $lang_pun_poll['Able revote'] ?></label></div>
			</fieldset>
		</fieldset>
		<fieldset class="frm-set set<?php echo ++$forum_page['set_count'] ?>">
			<div class="frm-buttons">
				<span class="fld-label"><?php echo $lang_pun_poll['Summary count']?><input type="text" name="poll_ans_count" size="5%" value="<?php echo ($count_poll_ans != NULL) ? intval($count_poll_ans) : '2'; ?>"> <input type="submit" name="update_poll" value="Update poll"></span>
			</div>
		</fieldset>

	<?php

	}
}

